function extractPathnameSegments(t){const e=t.replace(/^\/+|\/+$/g,"").split("/");return{resource:e[0]||null,id:e[1]||null}}function constructRouteFromSegments(t){let e="";return t.resource&&(e+=`/${t.resource}`),t.id&&(e+="/:id"),e||"/"}export function getActivePathname(){return location.hash.replace(/^#/,"")||"/"}export function getActiveRoute(){return constructRouteFromSegments(extractPathnameSegments(getActivePathname()))}export function parseActivePathname(){return extractPathnameSegments(getActivePathname())}export function getRoute(t){return constructRouteFromSegments(extractPathnameSegments(t))}export function parsePathname(t){return extractPathnameSegments(t)}