class LoginView{getTemplate(){return'\n      <section class="container" aria-label="Login">\n        <div class="auth-card" role="form" aria-label="Login Form">\n          <h2>Login</h2>\n          <form id="loginForm">\n            <label for="email">Email</label>\n            <input type="email" id="email" name="email" required />\n\n            <label for="password">Password</label>\n            <input type="password" id="password" name="password" required autocomplete="current-password" />\n\n            <a href="#" class="link">Forgot password?</a>\n            <button type="submit" id="loginButton">Login</button>\n\n            <div class="footer">\n              Belum punya akun? <a href="#/register" class="link">Daftar di sini</a>\n            </div>\n          </form>\n          <p id="errorMessage" style="color: red;" aria-live="polite"></p>\n        </div>\n      </section>\n    '}getFormElement(){return document.querySelector("#loginForm")}getSubmitButton(){return document.querySelector("#loginButton")}getFormValues(){const e=this.getFormElement();return{email:e.email.value.trim(),password:e.password.value}}setSubmitButtonDisabled(e){this.getSubmitButton().disabled=e}setSubmitButtonText(e){this.getSubmitButton().textContent=e}showErrorMessage(e){document.querySelector("#errorMessage").textContent=e}navigateToHome(){window.location.hash="/"}}export default new LoginView;