export default class RegisterView{getTemplate(){return'\n      <section class="container" aria-label="Register">\n        <div class="auth-card" role="form" aria-label="Register Form">\n          <h2>Register</h2>\n          <form id="registerForm">\n            <label for="name">Nama</label>\n            <input type="text" id="name" name="name" required />\n\n            <label for="email">Email</label>\n            <input type="email" id="email" name="email" required />\n\n            <label for="password">Password</label>\n            <input type="password" id="password" name="password" required />\n\n            <button type="submit" id="registerButton">Daftar</button>\n          </form>\n          <p id="registerMessage" aria-live="polite"></p>\n        </div>\n      </section>\n    '}getFormElement(){return document.querySelector("#registerForm")}getSubmitButton(){return document.querySelector("#registerButton")}getFormValues(){const e=this.getFormElement();return{name:e.name.value.trim(),email:e.email.value.trim(),password:e.password.value}}setSubmitButtonDisabled(e){this.getSubmitButton().disabled=e}showMessage(e){document.querySelector("#registerMessage").textContent=e}navigateToLogin(){window.location.hash="#/login"}}