import{getAllStories,deleteStory}from"../utils/idb-helper.js";const SavedStoriesView={async init(){const e=document.getElementById("saved-stories-list");e.innerHTML="<p>Memuat cerita tersimpan...</p>";const t=await getAllStories();t.length?(e.innerHTML=`<div class="story-list">\n      ${t.map((e=>`\n        <article class="story-card saved-story-item" data-id="${e.id}">\n          <h3>${e.name}</h3>\n          <p>${e.description||""}</p>\n          <button class="delete-saved-story">Hapus</button>\n        </article>\n      `)).join("")}\n    </div>`,e.querySelectorAll(".delete-saved-story").forEach((e=>{e.addEventListener("click",(async t=>{const s=e.closest(".saved-story-item").dataset.id;await deleteStory(s),this.init()}))}))):e.innerHTML="<p>Belum ada cerita yang disimpan.</p>"}};export default SavedStoriesView;